INSERT INTO `job` (`id`, `age_range`, `apply_count`, `benefit`, `create_at`, `description`, `expired_date`, `gender`, `like_count`, `number_of_recruitement`, `reponsibility`, `salary`, `status`, `title`, `trial_time`, `update_at`, `view_count`, `year_of_experience`, `category_id`, `enterprise_id`, `location_id`, `rank_id`, `working_model_id`, `address`, `working_address`, `working_time`, `jobcategory_id`) VALUES
	(20, '25 - 35 tuổi', 3, '<p>LƯƠNG CB : từ 23m~ + trợ cấp nhà 1m5 = &gt; 24m5</p><p>Tăng ca tb : từ 30h</p><p>Có thưởng: 1 năm 2 lần</p><p>+ Thời gian làm: 8:00 - 17:00</p><p>Ngày nghỉ : 1 năm trên 116 ngày( tuần 2 buổi và các kì nghỉ lễ lớn khác )</p><p>Thời gian thử việc : 3 tháng</p>', '2024-12-14 08:51:32.650000', '<p>Vận hành máy MC マシニングセンタ gia công 5 mặt cỡ lớn.</p><h2><strong>JOB hoàn toàn miễn phí</strong></h2>', '2025-05-30', 'NOTREQUIRE', NULL, 6, '<p>+ Tốt nghiệp các ngành thiết kế cơ khí, điện tử,,,</p><p>+ Có kinh nghiệm vận hành máy MC マシニングセンタ gia công 5 mặt cỡ lớn, biết thay mã hàng, có kiến thức về lập trình</p><p>+ Tiếng Nhật giao tiếp tốt</p>', 'từ 23m~ + trợ cấp', 'APPROVED', 'Tuyển gấp Kỹ sư cơ khí làm việc tại AICHIKEN-INUYAMA', '3 tháng', '2025-02-11 08:06:50.216000', 28, 'Over 300 member', 2, 12, 4, 1, 1, NULL, 'Nơi làm: AICHIKEN-INUYAMA', '08:00 AM - 17:00 PM', 2),
	(23, '', 3, '<p>Hình thức : Nhân viên chính thức của công ty haken( Chế độ giống nv chính thức của xí nghiệp )</p><p>L CB : Từ 22m( chưa tính tăng ca ) + hỗ trợ chuyên cần 5s, hỗ trợ đổi ca 5s</p><p>Hoặc lương giờ : 1450</p><p>+ Ngoài ra: Thưởng 1 năm 2 lần ( từ năm 2 tùy kn và doanh thu của cty)</p><p>+ Tăng ca tb : từ 20h bận có thể trên 40h</p><p>+ Thời gian : Ca ngày 8:00-17:00 , ca đêm 17h50-2h35( có thể chọn làm tuần ca ngày, tuần ca đêm hoặc chuyên ca đêm)</p><p>+ Cơm trưa cty: 370yen</p><p>Ngày nghỉ : 1 năm 112 ngày</p><p>Thời gian thử việc : 3 tháng ( lương không đổi )</p>', '2024-12-14 09:11:53.328000', '<p>+ Vận hành máy NC 旋盤オペレーター( hãng okuma) Công việc đơn giản như: thay chip, chỉnh sửa, tháo lắp...</p><p>JOB hoàn toàn miễn phí, nhà ở cty thuê. Chi tiết ib</p>', '2025-08-25', 'MALE', NULL, 8, '<p>+ Tốt nghiệp các ngành thiết kế cơ khí,,,,</p><p>+ Có kinh nghiệm vận hành máy NC旋盤オペレーター、補正。</p><p>+ Tiếng Nhật giao tiếp tốt từ ( làm việc trực tiếp với ng Nhật )</p>', 'Từ 22m( chưa tính tăng ca )', 'APPROVED', 'Tuyển dụng 03 kỹ sư cơ khí tại YAMAGATA-KEN', '3 tháng ( lương không đổi )', '2025-02-11 07:58:32.777000', 58, 'Trên 3 năm', 1, 15, 37, 3, 3, NULL, 'YAMAGATA-KEN( xưởng sạch sẽ, khu vực sinh sống thuận tiện, gần siêu thị com,,,)', 'Theo ca', 16),
	(30, '25 - 35 tuổi', 5, '<p>Hình thức tuyển dụng : Nhân viên chính thức</p><p>Thử việc 6th lương từ 1450-1500yen,</p><p>Thu nhập sau thử việc : 400-600m</p><p>+ Trợ cấp khác: có</p><p>+ Ngoài ra: Thưởng 1 năm 2 lần ( từ năm 2 kể từ khi lên nv chính thức )</p><p>Tăng ca tb : từ 40h bận có thể trên 70h</p><p>+ Thời gian làm: Ca ngày 8:00-17:00, sau khi lên nv chính thức có thể làm ca đêm từ 20:5h sáng</p><p>Ngày nghỉ : 1 năm 114 ngày( tuần 2 buổi và nghỉ theo lịch của cty )</p>', '2024-12-16 03:35:31.619000', '<p>Sử dụng Gcode để lập trình, vận hành máy MC( マシニングセンタオペレーター)</p>', '2025-09-30', 'MALE', NULL, 6, '<p>+ Tốt nghiệp các ngành thiết kế cơ khí,thiết kế,điện tử,,,</p><p>+ Có kinh nghiệm vận hành máy MC(マシニングセンタオペレーター) hoặc máy tiện NC</p><p>+ Có kn dùng Gcode, gia công sp đơn chiếc</p><p>+ Tiếng Nhật giao tiếp tốt từ ( làm việc trực tiếp với ng Nhật )</p>', '40 man', 'APPROVED', 'J..OB MỚI!!! TD 1 nam KỸ SƯ CƠ KHÍ tại NAGOYASHI-MINATO', '6th lương từ 1450-1500yen', '2025-02-13 08:05:44.127000', 67, 'Over 300 member', 3, 3, 4, 3, 3, NULL, 'NAGOYASHI-MINATO ( cty gần ga, cạnh trung tâm thương mại lớn )', '09:00 AM - 17:00 PM', 3),
	(36, '25 - 35 tuổi', 2, '<p>LCB : Từ 22m + trợ cấp đổi ca 5sen+ 5sen chuyên cần</p><p>Hoặc LG: 1430/h ( nhân viên hợp đồng )</p><p>+ Tăng ca tb: 20h~40h</p><p>+ Ngoài ra: Thưởng 1 năm 2 lần( từ năm 2, chỉ theo chế độ lcb ), có tăng lương</p><p>+ Thời gian : Ca ngày 8:15 - 17:15 , ca đêm : 17:00-2:00( đổi ca )</p><p>+ Ngày nghỉ năm 115 ngày ( tuần 2 buổi t7,cn và các kì lễ lớn )</p><p>Thời gian thử việc : 3 tháng ( lương không đổi )</p>', '2025-01-02 08:02:25.807000', '<p>+ Vận hành máy NC, CNC để gia công cơ khí ( sp: bộ phận của oto&nbsp;</p>', '2025-05-30', 'MALE', NULL, 4, '<p>+ Tốt nghiệp các ngành cơ khí, thiết kế, điện tử, xây dựng,,,</p><p>+ Có kinh nghiệm vận hành máy NC,CNC</p><p>+ Tiếng Nhật giao cơ bản</p>', 'Từ 22m + trợ cấp', 'APPROVED', 'Tuyển bổ sung kỹ sư : 4nam - dành cho các bạn muốn cày xiền tại GIFU', '3 tháng ( lương không đổi )', '2025-02-11 07:59:29.858000', 118, 'Không yêu cầu', NULL, 17, 20, 4, 3, NULL, 'Tại GIFU-( công ty có 9 xưởng )', '08:00 AM - 17:00 PM', 3),
	(41, '25 - 35 tuổi', 4, '<p>LCB tháng từ : 20~26m ( tùy vào độ tuổi và kinh nghiệm thực tế ) chưa bao gồm trợ cấp và tăng ca.</p><p>+ Từ 27t + kinh nghiệm =&gt; Up 22-23m</p><p>+ Từ 35t + kinh nghiệm =&gt; Up 24 ~ 26m</p><p>Thưởng：năm ngoái tổng 6thang lương</p><p>Các khoản trợ cấp : Lên đến 8m trợ cấp</p><p>・Trợ cấp đổi ca : 2s5/1ngay =&gt; 1 tháng tb 2m5</p><p>・ Trợ cấp làm việc 3s/thang</p><p>・ Trợ cấp nhà ở 50% ( đối với người có gia đình )</p><p>・ Trợ cấp đi lại : Có</p><p>・ Trợ cấp Gđ : Vợ 2m, con 1s/ng từ 2 người up 2s5/ng</p><p>・ Trợ cấp làm đêm, tăng ca : Có</p><p>Thời gian làm việc : Đổi ca( theo tuần ) - làm 4 nghỉ 2</p><p>1 năm nghỉ 130 ngày</p><p>+ Ngày làm : 8 tiếng 30p</p><p>・ Ca ngày : 8:00 - 17:30</p><p>・ Ca đêm : 19:00 - 4:30</p><p>Tăng ca： Trung bình:&nbsp; 30 giờ</p>', '2025-01-08 03:18:40.444000', '<p>Thay mã hàng, vận hành máy CNC cửa lớn ( 門型マシニングセンタ - Không yc kinh nghiệm đối với máy này )</p>', '2025-05-30', 'NOTREQUIRE', NULL, 6, '<p>&nbsp;Tốt nghiệp Đại học, cao đẳng, chuyên ngành cơ khí, Cơ điện tử...</p><p>- Có khả năng giao tiếp tiếng Nhật tốt</p><p>- Có kinh nghiệm vận hành máy CNC, thay mã hàng, gia công hàng đơn chiếc</p><p>- Ứng viên hiện đang ở Nhật, muốn gắn bó lâu dài với cty</p>', 'LCB tháng từ : 20~26m', 'APPROVED', 'Miễn phí: Tuyển dụng nhân viên chính thức - Kỹ sư vận hành máy CNC', '02 tháng', '2025-02-11 07:19:33.014000', 83, 'Có KN vận hành', NULL, 19, 3, NULL, 3, NULL, ' SHIZUOKA - OMAEZAKI ( Công ty có sẵn ký túc xá :9s/ tháng )', 'Theo ca', 3),
	(43, 'Không yêu  cầu', 5, '<p>Thu nhập:</p><ul><li>Lương cơ bản : 22M</li><li>Trợ cấp 2M5~&nbsp;</li><li>Tổng thu nhập =&gt;24M5&nbsp;</li></ul><p>Thời gian tăng ca: Tăng ca tb: khoảng 20h&nbsp;</p><p>Thời gian làm việc : 8:30 - 17:30&nbsp;</p><p>Ngày nghỉ năm 120 ngày ( theo lịch công ty)</p>', '2025-01-08 03:30:13.204000', '<p>Đọc bản vẻ, sơ đồ lắp ráp và lắp ráp bộ phận của máy mài</p>', '2025-10-30', 'MALE', NULL, 20, '<p>+ Tốt nghiệp các ngành cơ khí, thiết kế, điện tử, xây dựng,,,&nbsp;</p><p>+ Có kinh nghiệm đọc sơ đồ bản vẻ để lắp ráp cơ khí, oto,,,&nbsp;</p><p>+ Tiếng Nhật giao tiếp tốt từ N4 trở lên</p>', 'Trên 24M5', 'APPROVED', 'Tuyển dụng Tốt nghiệp các ngành cơ khí, thiết kế, điện tử, xây dựng', '03 tháng', '2025-02-10 08:44:26.520000', 84, 'Trên 1 năm', NULL, 18, 4, NULL, 3, NULL, 'AICHI-AMAGUN', '8:30 - 17:30', 3),
	(50, '', NULL, '<p>LƯƠNG CB : Từ 28m + 2m trợ cấp tay nghề</p><p>+ Tăng ca tb : 30h/ tháng</p><p>+ Ngoài ra: Thưởng 1 năm 2 lần ( từ năm 2 tùy kn và doanh thu của cty)</p><p>+ Thời gian làm:  </p><p>+ Ngày nghỉ: Tuần 2 buổi, 121ngay/năm( theo lịch của cty, có khả năng làm thêm t7 )</p><p>Thời gian thử việc : 3 tháng ( lương không đổi )</p>', '2025-02-11 08:13:08.964000', '<p>+ Sử dụng PLC để thiết kế và lắp đặt mạch điện, chạy thử,,,</p>', '2025-06-30', 'MALE', NULL, 6, '<p>+ Tốt nghiệp các ngành thiết kế cơ khí,,,,</p><p>+ Có kinh nghiệm dùng PLC trên 1 năm( Kn ở Vn ok )</p><p>+ Tiếng Nhật giao tiếp tốt, muốn làm việc lâu dài</p><p>+ Đọc được bản vẽ để lắp đặt mạch điện</p>', 'Từ 28m + 2m trợ cấp tay nghề', 'APPROVED', 'Kỹ sư thiết kế Sử dụng PLC để thiết kế và lắp đặt mạch điện, chạy thử', '', '2025-02-11 08:16:13.720000', 40, 'kinh nghiệm dùng PLC trên 1 năm', NULL, 11, 10, NULL, 3, NULL, 'Nơi làm: SHIZUOKA - HAMAMASTU', 'Ca ngày 8:20-17:25', 7),
	(51, '', NULL, '<p>Hình thức tuyển dụng : Nhân viên chính thức của công ty sản xuất</p><p>LƯƠNG CB : Từ 23m + Trợ cấp nhà ở 2m + chuyên cần 5s</p><p>+ Trợ cấp khác: Có</p><p>Tăng ca tb : từ 30-60h</p><p>+ Thời gian làm: 7:30 - 16:30</p><p>Ngày nghỉ : 1 năm trên 116 ngày</p><p>Thời gian thử việc : 3 tháng</p>', '2025-02-11 08:57:03.792000', '<p>Dùng Gcode để lập trình và vận hành máy tiện NC</p><p>JOB hoàn toàn miễn phí</p>', '2025-12-31', 'MALE', NULL, 10, '<p>+ Tốt nghiệp các ngành thiết kế, kỹ thuật cơ khí,,,</p><p>+ Có kinh nghiệm vận hành máy tiện NC( okuma) và biết dùng Gcode</p><p>+ Tiếng Nhật giao tiếp tốt</p>', 'Từ 23m + Trợ cấp', 'APPROVED', 'Tuyển dụng kỹ sư cơ khí - Dùng Gcode để lập trình và vận hành máy tiện NC', '3 tháng', '2025-02-11 08:58:20.757000', 15, '', NULL, 22, 44, NULL, 3, NULL, 'YAMANASHI - Nhật Bản', '7:30 - 16:30', 16),
	(52, '', NULL, '<p>・Lương cơ bản:    (bao gồm 30h làm thêm cố định)&nbsp; quá 30h tính riêng, có thể đàm phán lương theo năng lực</p><p>・Thưởng, tăng lương ： Thưởng 2 lần/năm(Tùy theo thành tích công ty), tăng lương 1 lần/năm.</p><p>・Chế độ: Bảo hiểm, Nenkin, Bảo hiểm tuyển dụng,…</p><p>・Ngày nghỉ: 1 năm nghỉ 99 ngày.</p><p>・Giờ làm việc：8:30~17:15【Giờ làm thực tế ７h45’, về giờ giấc có thể điều chỉnh linh hoạt theo nguyện vọng 】</p><p>・Hình thức: Nhân viên chính thức của công ty sản xuất (3 tháng thử việc, lương cơ bản 20万)</p>', '2025-02-11 09:06:46.958000', '<p>-Dùng phần mềm AUTOCAD, Jw-CAD,… để tạo bản vẽ và thiết kế, sản phẩm chủ yếu là các vật dụng dùng trong xây dựng, sản phẩm liên quan đến vật liệu như bê tông,..</p><p>- Đối ứng khách hàng, dạy việc.</p><p>-Đôi khi cũng có cả đi công tác trong nước và cả nước ngoài như Việt Nam, Trung Quốc,..</p><p>IB trực tiếp để nhận chi tiết về job và được hướng dẫn từ A~Z quy trình chuyển việc】</p><blockquote><p><strong>KHÔNG PHÍ, KHÔNG CỌC</strong></p></blockquote>', '2025-08-26', 'MALE', NULL, 10, '<p>- Tốt nghiệp Đại học, cao đẳng, chuyên ngành cơ khí, Cơ điện tử, Kiến trúc xây dựng...</p><p>- Có kinh nghiệm thiết kế trên 1 năm bằng các phần mềm thiết kế như AUTOCAD, Jw-CAD,…</p><p>- Có khả năng giao tiếp tiếng Nhật tương đương N2.</p><p>- Ứng viên hiện đang ở Nhật.</p>', '25~35万', 'APPROVED', 'Kỹ sư thiết kế (Chủ yế sử dụng AUTO CAD, Jw-CAD) - làm việc tại KYOTO', '', NULL, 24, '', NULL, 23, 16, NULL, 3, NULL, 'NƠI LÀM VIỆC : KYOTO－KAMEOKA', '8:30~17:15', 1),
	(53, '', 1, '<p>LƯƠNG CƠ BẢN : 30man + hỗ trợ nhà ở 1man ( chưa bao gồm tăng ca)</p><p>・ Tăng ca từ 20H~ ( lúc bận lên đến 80H )</p><p>・Ngày nghỉ: 1 năm nghỉ 108 ngày</p><p>・Giờ làm việc：8:00～17:10</p><p>・Thử việc: 3 tháng(lương không thay đổi)</p><p>=&gt; Sau 6 tháng lên nhân viên chính thức của xí nghiệp</p>', '2025-02-11 09:13:40.575000', '<p>Kỹ sư thiết kế khuôn nhựa - lương cao - tăng ca nhiều&nbsp; - làm việc tại HIROSHIMA</p><p>( Đã có 2 kĩ sư VN vận hành máy và lập trình )</p><p>- Dùng phần mềm CAD, NX,,, để thiết kế khuôn nhựa.</p>', '2025-05-30', 'MALE', NULL, 6, '<p>- Tốt nghiệp Đại học, cao đẳng, chuyên ngành cơ khí, thiết kế cơ khí,,,</p><p>- Có kinh nghiệm thiết kế khuôn nhựa, khuôn đúc hoặc khuôn máy dập,,,, biết sử dụng phần mềm CAD, NX hoặc các phần mềm thiết kế khác là một lợi thế.</p><p>- Có khả năng giao tiếp tiếng Nhật tương đương N3 trở lên.</p>', '30man', 'APPROVED', 'Kỹ sư thiết kế khuôn nhựa - lương cao - tăng ca nhiều  - làm việc tại HIROSHIMA', '3 tháng(lương không thay đổi)', '2025-02-19 02:58:04.393000', 43, '', NULL, 24, 15, NULL, 3, NULL, 'NƠI LÀM VIỆC : HIROSHIMA', '8:00～17:10', 4),
	(54, '', 1, '<p>Lương CB: 29m( chưa tính tăng ca)</p><p>Tăng ca tb: 20h</p><p>- Thưởng: Tuỳ theo năng lực và tình hình của cty ( 1 năm 2 lần kể từ năm thứ 2 )</p><p>Giờ làm: 8:30–17:30</p><p>- Trợ cấp: nhà ở: 1m+ chuyên cần: 5sen</p><p>- Phúc lợi xã hội: tham gia đầy đủ bảo hiểm xã hội</p><p>- Thời gian thử việc: 3 tháng( lương trợ cấp k thay đổi)</p><p>-Ngày nghỉ: 117ngày/năm( theo lịch của cty)</p>', '2025-02-11 09:21:47.699000', '<p>- Nội dung cv: Thiết kế cơ khí - Thiết kế máy mài</p>', '2025-08-30', 'MALE', NULL, 1, '<p>+ Có kinh nghiệm sử dụng 2DAD trong thiết kế cơ khí</p><p>+ Tiếng Nhật giao tiếp tốt</p><p>* Hỗ trợ pv, thuê nhà…</p><p>ĐƠN KHÔNG CỌC, KHÔNG PHÍ</p>', ' 29m( chưa tính tăng ca)', 'APPROVED', '(Miễn phí ) Tuyển gấp: 1 NAM kĩ sư Thiết kế cơ khí - Thiết kế máy mài tại AICHI-AMAGUN', '3 tháng( lương trợ cấp k thay đổi)', NULL, 31, '', NULL, 12, 4, NULL, 3, NULL, 'AICHI-AMAGUN', '8:30–17:30', 3),
	(55, '', NULL, '<p>+ Lương cơ bản: 30man + 2man trợ cấp nhà ở ( Có thưởng từ năm thứ 2- 1 năm 2 lần )</p><p>・Ngày nghỉ: 1 năm nghỉ 114 ngày</p><p>・Giờ làm việc：8:10～17:00</p><p>・Thử việc: 3 tháng(lương không thay đổi)</p>', '2025-02-11 09:28:23.876000', '<p>Dùng phần mềm Solidworks, MYPAC để thiết kế khuôn nhựa và khuôn đúc.</p>', '2025-08-30', 'MALE', NULL, 5, '<p>- Tốt nghiệp Đại học, cao đẳng, chuyên ngành cơ khí, Cơ điện tử, Kiến trúc xây dựng...</p><p>- Có kinh nghiệm thiết kế khuôn nhựa và khuôn đúc, biết sử dụng phần mêm Solidworks (hiện tại cty đang dùng phần mềm MYPAC) . Biết thêm cả làm chương trình cho máy MC là một lợi thế.</p><p>- Có khả năng giao tiếp tiếng Nhật tương đương N3 trở lên.</p><p>- Ứng viên hiện đang ở Nhật.</p>', '30man + 2man trợ cấp nhà ở', 'APPROVED', ' Kỹ sư thiết kế khuôn - Lương cao - làm việc tại AICHI－CHIRYU', '3 tháng(lương không thay đổi)', NULL, 35, '', NULL, 18, 4, NULL, 3, NULL, 'AICHI－CHIRYU', '8:10～17:00', 3),
	(56, '', 2, '<p>・Lương cơ bản : 21万</p><p>・Tăng ca： Trung bình 40 tiếng</p><p>・Thưởng： Từ năm hai trở đi (Tùy vào tình hình kinh doanh của cty)</p><p>・Tiền nhà： hỗ trợ trả toàn bộ tiền đầu vào, đồ đạc trong nhà tự sắm</p><p>・Ngày nghỉ: 1 năm nghỉ 118 ngày</p><p>・Giờ làm việc ：　 Xoay ca &nbsp; &nbsp;8: 00～17：00 &nbsp; hoặc 17：00～8：00</p><p>・Hình thức: Nhân viên chính thức của công ty haken</p>', '2025-02-11 09:38:47.087000', '<p>Cty mở rộng nên tuyển bổ sung thêm</p><p>VỊ TRÍ : KỸ SƯ THỰC PHẨM làm việc cho Cty Cực Lớn( có các chi nhánh tại nước ngoài)</p><p>NƠI LÀM VIỆC : MIE-TSU SHI</p><p>THỜI GIAN, ĐỊA ĐIỂM PHỎNG VẤN : Ngay sau khi pass hồ sơ</p><p>Kiểm soát nhiệt độ và kiểm soát nồng độ được thực hiện trong quá trình sản xuất bột đậu đỏ cùng những công việc phụ liên quan trong xưởng.</p>', '2025-07-31', 'MALE', NULL, 5, '<p>- Tốt nghiệp Đại học, cao đẳng, chuyên ngành thực phẩm.</p><p>- Có khả năng giao tiếp tiếng Nhật tương đương N4 cứng</p><p>- Ứng viên hiện đang ở Nhật Bản</p>', '21万', 'APPROVED', 'KỸ SƯ THỰC PHẨM làm việc cho Cty Cực Lớn( có các chi nhánh tại nước ngoài)', '', NULL, 49, '', NULL, 24, 25, NULL, 3, NULL, 'NƠI LÀM VIỆC : MIE-TSU SHI', 'Xoay ca', 6),
	(57, 'Không yêu  cầu', NULL, '<p>LCB 6 tháng thử việc : 23m~</p><p>Lương sau 6 tháng từ : 23~35m( bao gồm các khoản trợ cấp )</p><p>+ Trợ cấp :&nbsp;</p><p>・ Gia đình: vợ 1m + con 5s</p><p>・ Trợ cấp chuyên cần : 2m</p><p>・ Trợ cấp đi lại : có</p><p>+ Tăng ca trung bình: ~10h</p><p>+ Ngoài ra: Thưởng 1 năm 2 lần</p><p>+ Thời gian : Ca ngày 7:45 - 17:15</p><p>+ Ngày nghỉ năm 120 ngày ( Theo lich của cty)</p>', '2025-02-13 07:59:42.849000', '<p>Nhân viên chính thức của haken( vô thời hạn), sau đó sẽ chuyển lên NHÂN VIÊN CHÍNH THỨC của xí nghiệp</p>', '2025-08-29', 'MALE', NULL, 6, '<p>+ Tốt nghiệp các ngành cơ khí, thiết kế, điện tử, xây dựng,,,</p><p>+ Tiếng Nhật giao tiếp tốt</p><p>+ Có kinh nghiệm vận hành máy CNC và viết Gcode</p>', 'sau 6 tháng từ : 23~35m', 'APPROVED', 'Tuyển dụng kỹ sư cơ khí - nhân viên chính thức - tại HYOGO - MIKISHI', '6 tháng', NULL, 53, 'vận hành máy CNC và viết Gcode', NULL, 3, 14, NULL, 3, NULL, ' TẠI HYOGO - MIKISHI', '', 3);

