INSERT INTO `enterprise` (`id`, `address`, `approved_at`, `banner_name`, `create_at`, `follow_count`, `introduction`, `like_count`, `logo_name`, `name`, `number_of_employee`, `update_at`, `website`, `industrial_type`, `default_logo`, `user_id`) VALUES
	(3, 'Cầu Giấy, Hà Nội', NULL, NULL, '2024-12-10', NULL, '<p>hành lập từ năm 1999 dưới hình thức Công ty TNHH Biển Bạc, do nhu cầu phát triển và điều kiện thực tế, năm 2005 Công ty CP Biển Bạc được thành lập và thừa hưởng tất cả mọi quyền lợi và nghĩa vụ của Công ty TNHH Biển Bạc. Trải qua quá trình hình thành và phát triển, đến nay Biển Bạc là một trong những công ty hàng đầu trong lĩnh vực cung cấp thiết bị an ninh giám sát tại Việt Nam, đã được cấp chứng chỉ ISO 9001:2008 từ năm 2007 đến nay và đã đạt rất nhiều giải thưởng của các ban ngành, tổ chức trong và ngoài nước. Bằng kinh nghiệm và năng lực của mình, chúng tôi mong muốn sẽ mang lại cho Quý khách hàng những phương tiện, biện pháp quản lý, bảo vệ và điều hành hoạt động sản xuất kinh doanh, giúp Quý khách hàng có khả năng:“Tất cả trong tầm tay – Mọi sự trong tầm mắt”</p>', NULL, '1734331545003_5426952.png', 'Công Ty Cổ Phần Biển Bạc', '45', '2025-02-10 06:38:25.747000', 'tgg.com', 5, b'1', NULL),
	(4, ' Tòa nhà PV Gas, 673 Nguyễn Hữu Thọ, Phước Kiển, TP Hồ Chí Minh', NULL, '1733812188258_22-495-171.jpg', '2024-12-10', NULL, '', NULL, '1733812188258_68686.png', 'Công Ty TNHH Một Thành Viên Cơ Khí Hà Nội', '666', '2025-01-20 09:19:23.361000', 'abc.com', 5, b'0', NULL),
	(6, 'Phòng 2904, tháp A, chung cư thăng long Number one, 289 Khuất Duy Tiến, Trung Hòa, Cầu Giấy, HN', NULL, NULL, '2024-12-10', NULL, 'Công ty cổ phần hóa chất xây dựng Bách Khoa – Tiền thân là Công ty TNHH xây dựng và phát triển vật liệu mới Việt Khoa, được thành lập từ năm 2007, chuyên thi công trọn gói (cung cấp vật tư và nhân công thi công) hạng mục chống thấm, sơn sàn epoxy, chống thấm gốc Polyurethane, chống thấm màng cao phân tử cho nhà xưởng, nhà công nghiệp, TTTM, các khu công nghiệp, các dự án…..Trải qua những năm tháng xây dựng và phát triển được hợp tác với nhiều dự án trọng điểm như: Trụ sở Bộ công an, Trụ sở Bộ công thương, Trụ sở Tổng cục Hải Quan, Tập đoàn Vingroup và Taisei Nhật (nhà ga T2 Nội Bài)…đến nay Bách Khoa trở thành một trong những đơn vị có uy tín hàng đầu trong lĩnh vực chống thấm và sơn sàn epoxy.', NULL, '1734331883339_1576566561.png', 'Công Ty CP Hóa Chất Xây Dựng Bách Khoa', '10 - 150 people', '2024-12-16 06:51:23.339000', '', 6, NULL, NULL),
	(7, 'P602 Hồ Gươm Plaza, 102 Đ. Trần Phú, P. Mộ Lao, Q. Hà Đông, TP Hà Nội.', NULL, NULL, '2024-12-10', NULL, '* Tên công ty: Công ty cổ phần tư vấn đầu tư xây dựng Điện & Hạ tầng kỹ thuật\r\n* Quy mô công ty: 70 cán bộ, kỹ sư, cử nhân.\r\n* Lĩnh vực hoạt động:\r\nHoạt động trong các lĩnh vực xây dựng khảo sát, thiết kế, giám sát, thi công các công trình dân dụng, công nghiệp và hạ tầng kỹ thuật, công trình điện lực trên phạm vi toàn quốc từ vùng đô thị, đồng bằng, miền núi, biên giới, hải đảo.\r\n* Số năm hoạt động: Trên 10 năm.', NULL, '1735658718383_logo_173034676511.jpg', 'Công Ty Cổ Phần Tư Vấn Đầu Tư Xây Dựng Điện & Hạ Tầng Kỹ Thuật', '10 - 150 people', '2024-12-31 15:25:18.383000', '', 7, NULL, NULL),
	(8, 'VP1 - 85D6 - Khu ĐTM Đại Kim, P.Đại Kim, Q.Hoàng Mai, TP. Hà Nội', NULL, '1733800384098_22-495-171.jpg', '2024-12-10', NULL, 'VP1 - 85D6 - Khu ĐTM Đại Kim, P.Đại Kim, Q.Hoàng Mai, TP. Hà Nội', NULL, '1734332010387_1521693838_TS.jpg', 'Công ty CP đá Granite & Marble Tự Nhiên Thiên Sơn', '444', '2024-12-16 06:53:30.387000', 'tgg.comd', 3, NULL, NULL),
	(9, '103A-105-107 Nguyễn Thông, Phường 09, Quận 3, Thành phố Hồ Chí Minh', NULL, '1733803773439_Bruno.jpg', '2024-12-10', NULL, 'Giới thiệu công ty\r\nCông ty Cổ phần BraveSoft Việt Nam chuyên cung cấp dịch vụ phát triển phần mềm offshore, tập trung vào việc cung cấp giải pháp công nghệ tiên tiến cho khách hàng. Với phương châm "Cùng làm việc, cùng vui chơi", BraveSoft cam kết mang đến những sản phẩm và dịch vụ chất lượng cao, phù hợp với nhu cầu của thị trường. Công ty thực hiện phát triển hệ thống web và ứng dụng di động cho cả hệ điều hành iOS và Android, giúp khách hàng tối ưu hóa hoạt động kinh doanh của mình.\r\n\r\nMột trong những điểm mạnh của BraveSoft là mô hình phát triển "Lab Model", trong đó công ty tổ chức một đội ngũ phát triển chuyên biệt cho từng dự án. Điều này cho phép doanh nghiệp giữ được sự linh hoạt và hiệu quả cao trong quy trình làm việc. Ngoài ra, công ty còn thực hiện các dự án phát triển theo hợp đồng và giới thiệu các sản phẩm độc lập mà mình đã thiết kế, nghiên cứu và phát triển.\r\n\r\nBraveSoft đã có nhiều thành tựu đáng kể trong việc phát triển ứng dụng và hệ thống web, bao gồm một số sản phẩm tiêu biểu như hệ thống trang điện tử cung cấp thông tin, hệ thống chat trực tuyến, cũng như nhiều ứng dụng phục vụ nhu cầu người dùng khác. Công ty không ngừng tìm kiếm cơ hội phát triển những sản phẩm mới, hứa hẹn mang lại giá trị gia tăng cho khách hàng và người tiêu dùng.\r\nChính sách bảo hiểm\r\nĐược hưởng bảo hiểm sức khỏe.\r\nĐược hưởng bảo hiểm xã hội.\r\nDu lịch, tham gia các hoạt động ngoại khóa, sự kiện.\r\nCác hoạt động ngoại khóa\r\nDu lịch\r\nTeam building\r\nThể thao\r\nParty\r\nLịch sử thành lập\r\n2013: Công ty Cổ phần BraveSoft Việt Nam được thành lập', NULL, '1735658808416_logo cnc_173441903849.jpg', 'Công Ty Cổ Phần Bravesoft Việt Nam ', '56', '2024-12-31 15:26:48.416000', 'abcd.com', 1, NULL, NULL),
	(11, '292 Ỷ La- Dương Nội- Hà Đông- Hà Nội', NULL, '1733823630899_', '2024-12-10', NULL, '<p>Công ty cổ phần đầu tư Vindecom được có trụ sở tại phòng 1725 nhà CT5B Khu đô thị Xala, Hà Đông, Hà Nội;&nbsp;</p><p><strong>Ngành nghề chính</strong>: Thực hiện các d<a href="d">ự án cung cấp thiết </a>bị điều hòa nhiệt độ, Máy tính máy in, máy chiếu và các thiết bị giáo dục cho khối các đơn vị hành chính sự nghiệp trên địa bàn thành phố Hà Nội.</p><p><strong>Địa điểm kinh doanh:</strong>&nbsp;</p>', NULL, '1761795733578_about.jpg', 'Công Ty CP Đầu Tư Vindecom', '10 - 150 people', '2025-10-30 03:42:13.578000', '', 5, b'0', NULL),
	(12, 'Số 25, phố Lạc Trung, Phường Vĩnh Tuy, Quận Hai Bà Trưng, Thành phố Hà Nội,', NULL, '1733880771761_2024-06-09.jpg', '2024-12-11', NULL, '<p>Công ty Cổ phần Kỹ Thuật EDC VN được thành lập từ năm 2016 là công ty hoạt động trong lĩnh vực tư vấn thiết kế và cải tạo điện dân dụng, điện nhẹ cho văn phòng và showroom.</p><p>&nbsp;</p><p>&nbsp;Đến nay, công ty đã thiết kế và thi công nhiều công trình văn phòng nước ngoài và các hệ thống ngân hàng. Các công trình tập trung chủ yếu tại các tòa nhà văn phòng cao cấp tại khu vực Hà Nội.</p>', NULL, '1734331461843_162254615921.jpeg', 'Công Ty Cổ Phần Kỹ Thuật Edc VN', '10 - 150 people', '2025-12-04 09:43:53.672000', 'abcdd.com.vn', 5, b'1', NULL),
	(13, 'Tầng 1, 2, 6 Tòa nhà Metropolitan, 235 Đồng Khởi, Phường Bến Nghé, Quận 1, Thành phố Hồ Chí Minh, Việt Nam', NULL, '1733882405514_2024-06-09.jpg', '2024-12-11', 250, '<p>Năm 1870 HSBC mở văn phòng đầu tiên tại Sài Gòn (nay là thành phố Hồ Chí Minh).&nbsp;</p><p>Tháng Tám năm 1995, chi nhánh tại thành phố Hồ Chí Minh được cấp phép hoạt động và cung cấp đầy đủ các dịch vụ tài chính ngân hàng. HSBC khai trương chi nhánh thứ hai tại Hà Nội và thành lập văn phòng đại diện tại thành phố Cần Thơ vào năm 2005.</p>', NULL, '1764841525495_WpgsQu5D8gv8S.jpg', 'Ngân Hàng Trách Nhiệm Hữu Hạn Một Thành Viên HSBC', 'Over 300 people', '2025-12-04 09:45:25.495000', '', 4, b'0', NULL),
	(14, 'Số nhà 178, tổ 4, Phường Kiến Hưng, Quận Hà Đông, Hà Nội', NULL, '1734494438068_', '2024-12-18', NULL, 'CÔNG TY CỔ PHẦN TẬP ĐOÀN TDI\r\n- Tổng thầu xây dựng dân dụng và công nghiệp\r\n- Tổng thầu lắp đặt hệ thống cơ điện (ME).\r\n- Cung cấp dịch vụ thiết kế mô hình BIM trên nền tảng BIM 360', NULL, '1734494438068_TDI.jpeg', 'Công Ty Cổ Phần Công Nghệ Và Tư Vấn Á Châu', '10 - 150 people', '2025-01-03 01:49:43.243000', '', 4, NULL, NULL),
	(15, 'Lô E5-4, Khu công nghiệp Đại Đồng - Hoàn Sơn, Xã Đại Đồng', NULL, '1735826672021_', '2025-01-02', NULL, '<p>Công ty TNHH Cedo Việt nam là doanh nghiệp 100% vốn đầu tư của Anh Quốc, thuộc Tập đoàn Cedo, có trụ sở tại 06 nước Châu Âu, và Việt Nam Sản phẩm của Nhà máy Cedo Việt nam là các loại túi nilon sử dụng trong ngành thực phẩm, được xuất khẩu sang thị trường Châu Âu.</p>', NULL, '1735868757491_1364715909.jpeg', 'Công Ty TNHH Cedo (Việt Nam)', '20', '2025-12-04 09:44:16.162000', '', 5, b'0', NULL),
	(16, 'Khu Công nghệ Cao Tuần Châu, Hạ Long', NULL, '1736093746348_', '2025-01-05', NULL, '<h3>CHuyên dịch vụ khai thác</h3><p>Dịch vụ truyển hình</p>', NULL, '1761804833344_logo_eplus.png', 'Công ty Cổ phần dầu khí Bắc Á', 'Trên 500 nhân viên', '2025-12-04 09:44:05.809000', '', 7, b'0', NULL),
	(17, 'Lot N, Street No.26, Song Than Industrial Zone No.2, Di An District, Binh Duong Province', NULL, '1736306171827_', '2025-01-08', NULL, '<p>Hello</p><p><strong>Khánch</strong></p>', NULL, '1736306171827_perfetti.jpg', 'Perfetti Van Melle (Viet Nam)', '500 cán bộ/nhân viên', '2025-04-16 07:52:20.873000', 'www.perfettivanmelle.com', 1, b'0', NULL),
	(18, 'Phường Phú Hữu, Quận 9, TP. HCM', NULL, '1736306850829_', '2025-01-08', 120, '<p>Samco Vina cung cấp đa dạng các loại xe nâng, bao gồm xe nâng điện, xe nâng diesel, xe nâng container, xe nâng palet, và nhiều loại xe nâng chuyên dụng khác.&nbsp;</p><p>Hệ thống sản phẩm của công ty bao gồm cả xe nâng mới và xe nâng đã qua sử dụng, cùng với phụ tùng thay thế cho các loại xe như Kalmar hay Caterpillar. Bên cạnh đó, công ty cũng cung cấp dịch vụ cho thuê xe nâng, giúp khách hàng có thêm lựa chọn phù hợp với nhu cầu sử dụng.</p>', NULL, '1736306850829_SamcoVina.jpg', 'Công Ty CP Samco Vina', 'Trên 100 nhân viên', '2025-01-17 04:31:46.040000', '', 8, NULL, NULL),
	(19, 'Hàng Châu - Trung Hoa', NULL, '1737088676224_', '2025-01-17', NULL, '<p>Kinh nghiệm hơn 15 năm hoạt động trong lĩnh vực phòng cháy chữa cháy của nhà sáng lập, PHÚC BẢO AN HOLDING mang tới cho khách hàng các dịch vụ về phòng cháy chữa cháy toàn diện và giải pháp trọn vẹn nhất. Chúng tôi tin rằng chất lượng của mỗi công trình không chỉ nằm ở vẻ đẹp bên ngoài mà phải đảm bảo an toàn ở mức cao nhất cho tính mạng của con người và tài sản, cốt lõi của mỗi công trình phải được xây bằng “Tâm – Tín – Tầm” của chính những người tạo ra sản phẩm, dịch vụ.</p><p>&nbsp;</p><p>Với tôn chỉ mục đích ấy, <strong>PHÚC BẢO AN HOLDING</strong> đặt chữ Tâm lên hàng đầu, lấy chữ Tín làm vũ khí cạnh tranh, bảo vệ chữ Tín như bảo vệ sinh mạng mình để xứng Tầm thương hiệu Việt Nam – Đẳng cấp chất lượng toàn cầu và tự hào ghi lại các dấu ấn công trình trên khắp mọi miền tổ quốc với tư cách là chuyên gia Phòng cháy chữa cháy hàng đầu Việt Nam.</p>', NULL, '1766565108293_th.jpg', 'Công Ty TNHH Phúc Bảo An6', 'Trên 200 nhân viên', '2025-12-24 08:31:48.293000', 'abcdd.com.vn', 4, b'0', 88),
	(22, 'Tầng ,1, 2, 6 Tòa nhà Metropolitan, 235 Đồng Khởi, Phường Bến Nghé, Quận 1, Thành phố Hồ Chí Minh, Việt Nam', NULL, NULL, '2025-12-15', NULL, ' Giới thiệu doanh nghiệpGiới thiệu doanh nghiệpGiới thiệu doanh nghiệpGiới thiệu doanh nghiệpGiới thiệu doanh nghiệpGiới thiệu doanh nghiệpGiới thiệu doanh nghiệpGiới thiệu doanh nghiệp', NULL, NULL, 'Công nghệ thực phẩm v', '10 - 150 people', '2025-12-16 09:33:35.876000', 'www.perfettivanmelle.com', 5, b'1', NULL),
	(23, 'M8, Đường Đinh Công Tráng, Phường Xuân Khánh, Quận Ninh Kiều, Thành phố Cần Thơ, Việt Nam', NULL, NULL, '2025-12-16', NULL, '<p>Công an Đà Nẵng vừa bắt giữ một người bị truy nã đặc biệt nguy hiểm về các hành vi làm giả tài liệu của cơ quan, tổ chức; sử dụng tài liệu giả của cơ quan, tổ chức và lừa đảo chiếm đoạt tài sản.</p>', NULL, NULL, 'Công ty Kỹ nghệ Hồng Hà', 'Over 302sadsad0 people', '2025-12-16 09:01:19.720000', 'vnexpress.net', 3, b'1', NULL);

